package lecture_7_Operators_and_For_loop;

public class Bitwise_operators_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println(34 << 1);
		System.out.println(34 << 2);
		System.out.println(34 >> 1);
		System.out.println(34 >> 2);
		System.out.println(-34 << 1);
		System.out.println(-34 << 2);
		System.out.println(-34 >> 1);
		System.out.println(-34 >> 2);

	}

}
